mapper.select:
SELECT * FROM interest_category WHERE i_cate_num IN
#{i_cate_num}
ORDER BY i_cate_num ASC

mapper.update:
UPDATE user_info 
	SET 
u_profile = #{u_profile}, u_name = #{u_name}, u_sex = #{u_sex}, 
u_birth = #{u_birth}, u_area = #{u_area}, u_intro = #{u_intro} 
	WHERE 
u_id = #{u_id}

UPDATE user_info
	SET u_pw = #{u_pw}
		WHERE u_id = #{u_id}

UPDATE my_join_meet
	SET my_join_star = 1
		WHERE u_id = #{u_id} AND m_num = #{m_num}

UPDATE my_join_meet
	SET my_join_star = 0
		WHERE u_id = #{u_id} AND m_num = #{m_num}
